export const useGetBlockByHash = async () => {
  try {
    const block = await provider.getBlock(
      "0x4f36f8ae9d4658fdc48592ee0bf23ae2747907ad2cc758ac0e4e2966d9353343"
    );

    if (block && block.transactions.length > 0) {
      // Lấy hash của giao dịch đầu tiên trong block
      const transactionHash =
        "0xb875c33e66602b76abb0acdf130803435c49ae7bc4306cbf1a90bc37bcbc2cbd";
      // Lấy chi tiết giao dịch từ transaction hash
      const transaction = await provider.getTransaction(transactionHash);
      if (!transaction) {
        console.log("Giao dịch không tồn tại");
        return;
      }
      // Khởi tạo interface từ ABI
      const contractInterface = new ethers.Interface(contractABISuppyChain);
      // Decode dữ liệu từ transaction.data
      const decodedData = contractInterface.decodeFunctionData(
        "storeUserSession",
        transaction.data
      );
      console.log("Block:", block);
      console.log("Decoded data:", decodedData);
    }
  } catch (error) {
    console.error("Get Block Failed: ", error);
  }
};

